import React, { Component } from "react";
import NavBarBrand from "./navbarbrand";
import NavBarSearch from "./navbarsearch";
import NavBarProfile from "./navbarprofile";
import NavBarToggleButton from "./navbartogglebutton";
import "./NavBar.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

class NavBar extends Component {
  state = {
    isActive: false
  };

  appendIsActiveClass() {
    let isActive = !this.state.isActive;
    this.setState({ isActive });
  }

  renderClassDinamic() {
    let classes = "nav-link";
    return this.state.isActive ? classes : classes + " active";
  }

  getVerticalNavBar() {
    return (
      <React.Fragment>
        <nav
          className={
            "navbar navbar-light bg-light mr-auto float-lg-left full-h"
          }
          style={{
            borderRight: "0.5px solid #9E9E9E",
            float: "left",
            width: "132px"
          }}
        >
          <ul className="nav nav-pills">
            <li className>
              <a className={this.renderClassDinamic()} onClick={this.appendIsActiveClass.bind(this)} href="#">
                <FontAwesomeIcon
                  icon="layer-group"
                  style={{
                    width: "40%",
                    height: "50px"
                  }}
                />
              </a>
            </li>
            <li className="nav-item">
              <FontAwesomeIcon
                icon="th-large"
                style={{
                  width: "40%",
                  height: "50px"
                }}
              />
            </li>
          </ul>
        </nav>
      </React.Fragment>
    );
  }

  getHorizontalNavBar() {
    return (
      <div>
        <nav
          className={"navbar navbar-expand-lg navbar-light bg-light"}
          style={{
            borderBottom: "0.5px solid #9E9E9E",
            padding: "0"
          }}
        >
          <NavBarToggleButton />
          <NavBarBrand />
          <NavBarSearch />
          <NavBarProfile />
        </nav>
      </div>
    );
  }

  render() {
    const { isHorizontal } = this.props;
    let navBar;
    if (isHorizontal === true) navBar = this.getHorizontalNavBar();
    else navBar = this.getVerticalNavBar();
    return <div>{navBar}</div>;
  }
}

export default NavBar;
